#include <set>
#include <unordered_set>
#include <unordered_map>
#include <stack>
#include<iomanip>
#include <ctype.h>
#include <algorithm>
using namespace std;

class Solution {
public:
	int uniqueLetterString(string s) {
		int m = 1000000007;
		long long res = 0;
		if (s.size() == 0)
			return 0;
		int n = s.size();
		map<int, vector<int>> mp;
		for (int i = 0; i < 26; i++)
		{
			char c = 'A' + i;
			for (int j = 0; j < n; j++)
			{
				if (s[j] == c)
					mp[i].push_back(j);
			}
			for (int j = 0; j < mp[i].size(); j++)
			{
				int le = 0;
				int ri = 0;
				int lest, leed, rist, ried;
				if (j == 0)
					lest = -1;
				else
					lest = mp[i][j-1];
				leed = mp[i][j];
				le = leed - lest;
				rist = mp[i][j];
				if (j == mp[i].size() - 1)
					ried = s.size();
				else
					ried = mp[i][j+1];
				ri = ried - rist;
				res += (le*ri) % m;
			}
		}
		return res%m;
	}
};
int main()
{
	Solution s;
	s.uniqueLetterString("ABC");
	//s.uniqueLetterString("");
    return 0;
}

